import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import './App.css';
import Now from '../components/Now';
import Days from '../components/Days';

class App extends Component {

  	state = {
		name : null,
		weather : null,
		icon : null,
		temp : 0,
		list : []
  	}

  	componentDidMount() {
		let today = moment().format("YYYY-MM-DD") + " 15:00:00";
		console.log(today);

		axios.get('https://api.openweathermap.org/data/2.5/weather?q=Zagreb&units=metric&lang=hr&appid=a033c80fc22a346b73c366e384f8f6f8').then(response => {
			this.setState({
				name : response.data.name,
				weather : response.data.weather[0].main,
				icon : 'http://openweathermap.org/img/w/' + response.data.weather[0].icon + '.png',
				temp : Math.round(response.data.main.temp)
			});
		});

		axios.get('https://api.openweathermap.org/data/2.5/forecast?q=Zagreb&units=metric&lang=hr&cnt=5&appid=a033c80fc22a346b73c366e384f8f6f8').then(response => {




			this.setState({
				list : response.data.list
			});

			console.log(response.data.list);
		});
  	}

  	render() {
		return (
			<div className="App">
				<Now
					name={this.state.name}
					weather={this.state.weather} 
					icon={this.state.icon}
					temp={this.state.temp} />

				<Days days={this.state.list}/>
			</div>
		);
  	}
}

export default App;
