import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import './App.css';
import Now from '../components/Now';
import Days from '../components/Days';
import FindCity from '../components/FindCity/FindCity';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			name : null,
			weather : null,
			icon : null,
			temp : 0,
			list : [],
			city : null
		}

		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		this.setState({city: event.target.value});
	}

	handleSubmit(event) {
		alert('A city was submitted: ' + this.state.city);
		event.preventDefault();
	}

  	componentDidMount() {

		let showForDays = [];
		for(let i = 0; i <= 5; i++) {
			showForDays.push(moment().add(i, 'days').format("YYYY-MM-DD") + " 15:00:00");
		}

		axios.get('https://api.openweathermap.org/data/2.5/weather?q=Zagreb&units=metric&lang=hr&appid=a033c80fc22a346b73c366e384f8f6f8').then(response => {
			this.setState({
				name : response.data.name,
				weather : response.data.weather[0].main,
				icon : 'http://openweathermap.org/img/w/' + response.data.weather[0].icon + '.png',
				temp : Math.round(response.data.main.temp)
			});
		});

		axios.get('https://api.openweathermap.org/data/2.5/forecast?q=Zagreb&units=metric&lang=hr&appid=a033c80fc22a346b73c366e384f8f6f8').then(response => {

			let list = response.data.list;
			let forecastList = [];

			for(let i = 0; i < list.length; i++) {
				if(showForDays.includes(list[i].dt_txt)) {
					forecastList.push(list[i]);
				}
			}

			this.setState({
				list : forecastList
			});

		});
	}

	showForcast = ( ) => {

	}

  	render() {
		return (
			<div className="App">
				<FindCity 
					changed={this.handleChange}
					clicked={this.handleSubmit} />
				<Now
					name={this.state.name}
					weather={this.state.weather} 
					icon={this.state.icon}
					temp={this.state.temp} />

				<Days days={this.state.list}/>
			</div>
		);
  	}
}

export default App;
